#$pyFunction
def GetLSProData(page_data,Cookie_Jar,m):
  import re
  match = re.findall('group-title="([^"]+)"',page_data)
  seccion_list = []
  for x in match:
    if x not in seccion_list:
      seccion_list.append(x)
  return seccion_list

